import { forwardRef, useEffect, useState } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import { styled } from "styled-components";
import CompanyLogo from "./company-logo";
import PreviousJobCard from "./previous-job-card";
import { colors, device, fontSize, fontWeight, space } from "../styles/theme";
import useViewport from "./hooks/useViewport";
import { CompanyData } from "./types";

const JobCards = styled(motion.ul)`
  display: flex;
  margin-right: ${space[32]};
  gap: ${space[24]};
  will-change: transform;
  list-style-type: none;

  @media ${device.tablet} {
    margin-top: ${space[32]};
  }
`;

const CarouselWrapper = styled(motion.div)`
  display: flex;
  flex-direction: column;
  flex-wrap: nowrap;
  align-items: flex-start;
  overflow: visible;
  gap: ${space[16]};
`;

const Companies = styled(motion.div)`
  display: flex;
  gap: ${space[32]};
  flex-wrap: nowrap;
  justify-content: center;
  align-items: center;
`;

const FramerText = styled.span`
  color: ${colors.black};
  font-weight: ${fontWeight["bold"]};
  font-size: ${fontSize[32]};
`;

const SmallerFramerText = styled(FramerText)`
  font-size: ${fontSize[24]};
`;

const variantsCompanyLogo = {
  active: {
    opacity: 1,
    filter: "grayscale(0)",
  },
  inactive: {
    opacity: 0.2,
    filter: "grayscale(1)",
  },
};

const cardWidth = 435;

interface Props {
  animate: string;
  variants: any;
  initial: string;
  stickyCarouselEndPosition: number;
}

export const Carousel = forwardRef<HTMLDivElement, Props>((props, ref) => {
  const { animate, variants, initial, stickyCarouselEndPosition } = props;

  const { isMobile } = useViewport();
  const { scrollYProgress } = useScroll();
  const [activeCompany, setActiveCompany] = useState("gitbook");
  const [relativeCardWidth, setRelativeCardWidth] = useState(0);

  useEffect(() => {
    const documentHeight =
      document.documentElement.scrollHeight || document.body.scrollHeight;

    setRelativeCardWidth((cardWidth + 20) / documentHeight);
  }, []);

  const roles = [
    {
      role: "Product Engineer",
      id: 1,
      company: "gitbook",
      companyDescription:
        "GitBook makes it easy to plan, centralize and share knowledge, from start to ship.",
      description:
        "I help build features that allow users to distribute their docs, such as publishing it to sites or syncing with Github.",
      dates: "2023 - Present",
      link: "https://www.gitbook.com",
    },
    {
      role: "Frontend Engineer",
      id: 2,
      company: "ticketswap",
      companyDescription:
        "Ticketswap is well-known in the Netherlands as the safest place to buy and sell tickets for events.",
      description:
        "I was responsible for the buy-flow, working on ticket personalization, custom currencies, and more.",
      dates: "May 2022 - June 2023",
      link: "https://www.ticketswap.com",
    },
    {
      role: "Lead Product Specialist",
      id: 3,
      company: "framer",
      companyDescription:
        "Framer is a web-based design tool which also renders React code, allowing for powerful designs with real components and APIs.",
      description:
        "As lead, I was responsible I received additional responsibilities like team strategy and onboarding new hires.",
      dates: "Oct. 2021 - Apr. 2022",
      link: "https://www.framer.com",
    },
    {
      role: "Product Specialist",
      id: 4,
      company: "framer",
      companyDescription:
        "I helped teams like Spotify, Airbnb, Shopify and more leverage code in Framer.",
      description:
        "For example, for EA Games I built a component to connect gamepads, helped import the Airbus design system, and connect barcode scanners to Shopify's prototypes.",
      dates: "Jan. 2020 - Oct. 2021",
      link: "https://www.framer.com",
    },
    {
      role: "Community & Support",
      id: 5,
      company: "framer",
      companyDescription:
        "I started out in a role dedicated to writing tutorials, recording videos, and providing support.",
      description:
        "As Framer could get technical quickly, I decided to learn Javascript and React. This sparked a passion for coding that I still have today.",
      dates: "Apr. 2019 - Dec. 2019",
      link: "https://www.framer.com",
    },
  ];

  const companies: CompanyData[] = [
    {
      company: "gitbook",
      link: "https://www.gitbook.com",
      logo: { desktop: <GitbookLogo />, mobile: <GitbookLogoMobile /> },
    },
    {
      company: "ticketswap",
      link: "https://www.ticketswap.com",
      logo: { desktop: <TicketswapLogo />, mobile: <TicketswapLogoMobile /> },
    },
    {
      company: "framer",
      link: "https://www.framer.com",
      logo: { desktop: <FramerLogo />, mobile: <FramerLogoMobile /> },
    },
  ];

  // x-position for Companies carousel
  const getLogosXPosition = () => {
    const outputArray = isMobile ? [0, -180, -400] : [0, -245, -532];
    const inputArray = isMobile
      ? [0, relativeCardWidth * 1.8, relativeCardWidth * 3.6]
      : [0, relativeCardWidth * 2.35, relativeCardWidth * 4.7];

    return useTransform(scrollYProgress, inputArray, outputArray);
  };

  // x-position for JobCards carousel
  const x = useTransform(
    scrollYProgress,
    [0, stickyCarouselEndPosition],
    [0, -1072]
  );

  return (
    <CarouselWrapper
      ref={ref}
      animate={animate}
      initial={initial}
      variants={variants}
    >
      <Companies style={{ x: getLogosXPosition() }}>
        {companies.map(({ company, link, logo }) => (
          <CompanyLogo
            key={company}
            company={company}
            link={link}
            logo={logo}
            animate={activeCompany === company ? "active" : "inactive"}
            initial={"inactive"}
            variants={variantsCompanyLogo}
            setActiveCompany={setActiveCompany}
          />
        ))}
      </Companies>

      <JobCards style={{ x }}>
        {roles.map((roleEntry) => (
          <PreviousJobCard
            key={roleEntry.id}
            roleEntry={roleEntry}
            activeCompany={activeCompany}
          />
        ))}
      </JobCards>
    </CarouselWrapper>
  );
});

